classdef SmokeTests < matlab.unittest.TestCase    % These smoketests ensure that all live scripts and shortcuts open and    % execute with no warnings or errors.        properties (TestParameter)        DemoFile = struct( ... 			'Part01_DataPrepFeatureExtraction', {@Part01_DataPrepFeatureExtraction}, ...			'Part02_Modeling', {@Part02_Modeling});        Shortcut = struct( ... 			'openPart01', {@openPart01}, ...			'openPart02', {@openPart02})    end            methods (TestClassSetup)                % Shut off graphics warnings        function killGraphicsWarning(testCase)            ws = warning('off', 'MATLAB:hg:AutoSoftwareOpenGL');            testCase.addTeardown(@()warning(ws));                    end                % Close any new figures created by doc        function saveExistingFigures(testCase)                        existingfigs = findall(groot, 'Type', 'Figure');            testCase.addTeardown(@()delete(setdiff(findall(groot, 'Type', 'Figure'), existingfigs)))                    end            end        methods (TestClassTeardown)        function closeEditor(~)            closeEditor;        end    end        methods (Test)        function demoShouldNotWarn(testCase, DemoFile)                   testCase.verifyWarningFree(DemoFile);        end                function shortcutShouldNotWarn(testCase, Shortcut)            testCase.verifyWarningFree(Shortcut);        end    endend